function toTitleCase(sentence) {
    const lowerCaseWords = ['a', 'an', 'and', 'the', 'in', 'on', 'at', 'by', 'for', 'of', 'with', 'to', 'but', 'or', 'nor', 'as'];
    function hasMultipleCaps(word) {
        return (word.match(/[A-Z]/g) || []).length >= 2;
    }
    return sentence
        .split('-')
        .map((word, index, arr) => {
            if (index === 0 || index === arr.length - 1 || hasMultipleCaps(word) || !lowerCaseWords.includes(word.toLowerCase())) {
                return word.slice(0, 1).toUpperCase() + word.slice(1);
            }
            return word.toLowerCase();
        })
        .join('-');
}
result = Array.from(result).map(function(genre) {
	return toTitleCase(genre);
});
</js>

