ruleToc.chapterName:
#{{$.order}} - {{$.title}}<js>
function toTitleCase(sentence) {
    const lowerCaseWords = ['a', 'an', 'and', 'the', 'in', 'on', 'at', 'by', 'for', 'of', 'with', 'to', 'but', 'or', 'nor', 'as'];

    function hasMultipleCaps(word) {
        return (word.match(/[A-Z]/g) || []).length >= 2;
    }

    return sentence
        .split(' ')
        .map((word, index, arr) => {
            const previousWord = arr[index - 1];

            if (
                index === 0 || 
                index === arr.length - 1 || 
                hasMultipleCaps(word) || 
                (previousWord && (previousWord.endsWith(':') || previousWord.endsWith('-'))) || 
                !lowerCaseWords.includes(word.toLowerCase())
            ) {
                return word.charAt(0).toUpperCase() + word.slice(1);
            }

            return word.toLowerCase();
        })
        .join(' ');
}
result = toTitleCase(result
	.replace(/\(.+\s[mM]ore!*\)/g, "")
	.replace(/Chapter\s\d+\s/g, "")
	.replace(/\)$/, "")
	.replace(/(^#\d+\s\-\s)\(/g, "$1"))
</js>

