ruleBookInfo.tocUrl:
@js:
let url = baseUrl;
if (!url.endsWith('/')) url += '/';
url += 'chapters?page=1';
url;

ruleToc.nextTocUrl:
@js:
let list = [];
// Force sequential pagination up to 100 pages (~10,000 chapters)
for (let i = 2; i <= 100; i++) {
  list.push(baseUrl.split('?')[0] + '?page=' + i);
}
list;
