@js:
// Force WebView for first TOC load, with safe stop
if(baseUrl.includes('-novel.html')){
  baseUrl + ',{"webView":true,"webViewTimeout":5000}';
}else{
  let m = baseUrl.match(/https:\/\/novelfull\.net\/([a-z0-9-]+)/i);
  m ? 'https://novelfull.net/' + m[1] + '-novel.html,{"webView":true,"webViewTimeout":5000}' : baseUrl;
}
