ruleToc.chapterName:
#{{$.order}} - {{$.title}}<js>
function toTitleCase(sentence){
  var lowers=['a','an','and','the','in','on','at','by','for','of','with','to','but','or','nor','as'];
  function multiCap(w){return (w.match(/[A-Z]/g)||[]).length>=2;}
  return sentence.split(' ').map(function(w,i,a){var p=a[i-1];if(i===0||i===a.length-1||multiCap(w)||(p&&(p.endsWith(':')||p.endsWith('-')))||!lowers.includes(w.toLowerCase()))return w.charAt(0).toUpperCase()+w.slice(1);return w.toLowerCase();}).join(' ');
}
result = toTitleCase(result.replace(/\s+/g,' ').trim());
</js>
